mapper.select:
SELECT count(t.f_id)
  FROM sys_authz_feature_list t
 WHERE t.f_parent = #{parent}

SELECT count(t.f_id)
  FROM sys_authz_feature_list t
 WHERE t.f_code = #{code}

	AND t.f_id != #{origin}

SELECT t.f_id, t.f_code, t.f_name, t.f_abb, t.f_url, t.f_path, t.f_type, t.f_icon, t.f_order, t.f_parent, t.f_visible FROM sys_authz_feature_list t
and t.f_name like CONCAT('%',#{model.name},'%')
and t.f_type = #{model.type}
and t.f_visible = #{model.visible}
