mapper.update:
UPDATE sys_authz_org_team t
   SET team_status = #{status}
 WHERE team_id = #{id}

mapper.select:
SELECT count(t.team_id)
  FROM sys_authz_org_team t
 WHERE t.team_name = #{name}
   AND t.dept_id = #{deptId}

	AND t.team_id != #{teamId}

SELECT count(t.u_id)
  FROM sys_authz_user_account t
 WHERE t.team_id = #{id}

SELECT t2.org_id, (select x.org_name from sys_authz_org_list x where x.org_id = t2.org_id) as org_name, t.dept_id, t2.dept_name, t.team_id, t.team_name, t.team_intro, t.team_uid, t.team_status, t.time24 FROM sys_authz_org_team t inner join sys_authz_org_dept t2 on t2.dept_id = t.dept_id
and t2.org_id = #{model.orgId}
and t.dept_id = #{model.deptId}
and t.team_name like '%'||#{model.name}||'%'
ORDER By t.time24 ASC
