mapper.update:
UPDATE SYS_AUTHZ_ORG_LIST t 
   SET ORG_STATUS = #{status}
 WHERE ORG_ID = #{id,jdbcType=INTEGER}

update SYS_AUTHZ_ORG_LIST t

	t.ORG_CODE = #{code},
	t.ORG_NAME = #{name},
	t.ORG_INTRO = #{intro},
	t.ORG_STATUS = #{status}

where t.ORG_ID = #{id,jdbcType=INTEGER}

mapper.select:
SELECT count(t.ORG_ID) 
  FROM SYS_AUTHZ_ORG_LIST t 
 WHERE t.ORG_PARENT = #{parent,jdbcType=INTEGER}

SELECT count(t.DEPT_ID) 
  FROM SYS_AUTHZ_ORG_DEPT t 
 WHERE t.ORG_ID = #{id,jdbcType=INTEGER}

SELECT count(t.ORG_ID) 
  FROM SYS_AUTHZ_ORG_LIST t 
 WHERE t.ORG_CODE = #{code}

	AND t.ORG_ID != #{origin,jdbcType=INTEGER}

SELECT count(t.ORG_ID) 
  FROM SYS_AUTHZ_ORG_LIST t 
 WHERE t.ORG_NAME = #{name}

	AND t.ORG_ID != #{origin,jdbcType=INTEGER}
