mapper.update:
UPDATE SYS_AUTHZ_ORG_STAFF t 
   SET STAFF_STATUS = #{status}
 WHERE STAFF_ID = #{id}

update SYS_AUTHZ_ORG_STAFF t
t.ORG_ID = #{orgId}, t.DEPT_ID = #{deptId}, t.TEAM_ID = #{teamId}, t.POST_ID = #{postId}, t.STAFF_INTRO = #{intro}, t.TIME24 = #{time24}
where t.STAFF_ID = #{id}

mapper.select:
SELECT t.ORG_ID, (select x.ORG_NAME from SYS_AUTHZ_ORG_LIST x where x.ORG_ID = t.ORG_ID) as ORG_NAME, t.DEPT_ID, (select x.DEPT_NAME from SYS_AUTHZ_ORG_DEPT x where x.DEPT_ID = t.DEPT_ID) as DEPT_NAME, t.POST_ID, (select x.POST_NAME from SYS_AUTHZ_ORG_POSITION x where x.DEPT_ID = t.DEPT_ID AND x.POST_ID = t.POST_ID) as POST_NAME, t.TEAM_ID, (select x.TEAM_NAME from SYS_AUTHZ_ORG_TEAM x where x.DEPT_ID = t.DEPT_ID AND x.TEAM_ID = t.TEAM_ID) as TEAM_NAME, t.STAFF_ID, t.STAFF_INTRO, t.STAFF_STATUS, t.TIME24, t2.U_USERNAME, t2.U_ALIAS, t2.U_AVATAR, t2.U_PHONE, t2.U_EMAIL FROM SYS_AUTHZ_ORG_STAFF t inner join SYS_AUTHZ_USER_LIST t2 on t2.U_ID = t.STAFF_ID
and t.ORG_ID = #{model.orgId}
and t.DEPT_ID = #{model.deptId}
and t.TEAM_ID = #{model.teamId}
and t.POST_ID = #{model.postId}
and ( t2.U_USERNAME like CONCAT('%',#{model.name},'%') OR t2.U_ALIAS like CONCAT('%',#{model.name},'%') )
ORDER By t.TIME24 ASC
