mapper.update:
UPDATE SYS_AUTHZ_ORG_POST t
   SET POST_STATUS = #{status}
 WHERE POST_ID = #{id}

update SYS_AUTHZ_ORG_POST t
t.DEPT_ID = #{deptId}, t.POST_CODE = #{code}, t.POST_NAME = #{name}, t.POST_INTRO = #{intro}, t.POST_STATUS = #{status}
where t.POST_ID = #{id}

mapper.select:
SELECT count(t.POST_ID) 
  FROM SYS_AUTHZ_ORG_POST t 
 WHERE t.POST_CODE = #{code}

	AND t.POST_ID != #{origin}

SELECT count(t.POST_ID) 
  FROM SYS_AUTHZ_ORG_POST t 
 WHERE t.POST_NAME = #{name}

	AND t.POST_ID != #{origin}

SELECT t2.ORG_ID, (select x.ORG_NAME from SYS_AUTHZ_ORG_LIST x where x.ORG_ID = t2.ORG_ID) as ORG_NAME, t.DEPT_ID, t2.DEPT_NAME, t.POST_ID, t.POST_CODE, t.POST_NAME, t.POST_INTRO, t.POST_UID, t.POST_STATUS, t.TIME24 FROM SYS_AUTHZ_ORG_POST t inner join SYS_AUTHZ_ORG_DEPT t2 on t2.DEPT_ID = t.DEPT_ID
and t2.ORG_ID = #{model.orgId}
and t.DEPT_ID = #{model.deptId}
and t.POST_NAME like '%'||#{model.name}||'%'
ORDER By t.TIME24 ASC
