mapper.select:
   SELECT COUNT(1)
	 FROM t_user_profile t
	WHERE t.phone = #{phone}

	   AND t.id != #{origin}

	AND ROWNUM = 1

   SELECT COUNT(1)
	 FROM t_user_profile t
	WHERE t.email = #{email}

	  AND t.id != #{origin}

	AND ROWNUM = 1

   SELECT COUNT(1)
	 FROM t_user_profile t
	WHERE t.idcard = #{idcard}

	  AND t.id != #{origin}

	AND ROWNUM = 1
