mapper.update:
update SYS_AUTHZ_USER_LIST t
t.U_CODE = #{ucode}, t.U_APP_ID = #{appId}, t.U_APP_CHANNEL = #{appChannel}, t.U_APP_VERSION = #{appVer}, t.U_ONLINE = #{online}, t.U_STATUS = #{status},
where t.U_ID = #{id}

update SYS_AUTHZ_USER_LIST t 
   set t.U_PASSWORD = #{password}
 where t.U_ID = #{userId}

update SYS_AUTHZ_USER_LIST t 
   set t.U_PASSWORD = #{password}
where t.U_ID = #{userId} 
  AND t.U_PASSWORD = #{oldPassword}

UPDATE SYS_AUTHZ_USER_LIST t
   SET t.U_STATUS = #{status}
 WHERE t.U_ID = #{userId}

mapper.select:
SELECT count(t.U_ID) 
  FROM SYS_AUTHZ_USER_LIST t 
 WHERE t.U_USERNAME = #{name}

	AND t.U_ID != #{origin}

SELECT count(t.U_ID) 
  FROM SYS_AUTHZ_USER_LIST t 
 WHERE t.U_CODE = #{code}

	AND t.U_ID != #{origin}

SELECT t.R_ID, t.R_KEY, t.R_NAME, t.R_TYPE, t.R_INTRO, t.R_STATUS, (SELECT COUNT(x.R_ID) FROM SYS_AUTHZ_USER_ROLE_RELATION x WHERE x.R_ID = t.R_ID) as R_USERS, t.R_TIME24 FROM SYS_AUTHZ_ROLE_LIST t INNER JOIN SYS_AUTHZ_USER_ROLE_RELATION x ON t.R_ID = x.R_ID WHERE x.U_ID = #{id}
and t.R_STATUS = #{model.status}
and t.R_TYPE = #{model.type}
and t.R_NAME like CONCAT('%',#{model.name},'%')

SELECT t.R_ID, t.R_KEY, t.R_NAME, t.R_TYPE, t.R_INTRO, t.R_STATUS, (SELECT COUNT(x.R_ID) FROM SYS_AUTHZ_USER_ROLE_RELATION x WHERE x.R_ID = t.R_ID) as R_USERS, t.R_TIME24 FROM SYS_AUTHZ_ROLE_LIST t WHERE NOT EXISTS ( SELECT '1' FROM SYS_AUTHZ_USER_ROLE_RELATION z WHERE t.R_ID = z.R_ID and z.U_ID = #{id})
and t.R_STATUS = #{model.status}
and t.R_TYPE = #{model.type}
and t.R_NAME like CONCAT('%',#{model.name},'%')
