mapper.update:
   UPDATE sys_authz_user_thirdparty t
	  SET t.u_id = #{uid}
	WHERE t.t_unionid = #{unionid}

 UPDATE sys_authz_user_thirdparty t
  SET t.u_id = #{uid}
WHERE t.t_openid = #{openid}

mapper.select:
SELECT COUNT(1)
  FROM sys_authz_user_thirdparty t
 WHERE t.u_id = #{uid}
   AND t.t_type = #{type}

	AND t.t_id != #{id}

   AND ROWNUM = 1

SELECT COUNT(1)
  FROM sys_authz_user_thirdparty t
 WHERE t.u_id = #{uid}
   AND t.t_type = #{type}
   AND ROWNUM = 1
