mapper.select:
   SELECT COUNT(1)
	 FROM sys_authz_user_profile t
	WHERE t.u_phone = #{phone}

	   AND t.u_pid != #{origin}

	  AND ROWNUM = 1

   SELECT COUNT(1)
	 FROM sys_authz_user_profile t
	WHERE t.u_email = #{email}

	   AND t.u_pid != #{origin}

	AND ROWNUM = 1

   SELECT COUNT(1)
	 FROM sys_authz_user_profile t
	WHERE t.u_idcard = #{idcard}

	   AND t.u_pid != #{origin}

	 AND ROWNUM = 1
