mapper.update:
   UPDATE SYS_AUTHZ_USER_THIRDPARTY t 
	  SET t.U_ID = #{uid}
	WHERE t.T_UNIONID = #{unionid}

 UPDATE SYS_AUTHZ_USER_THIRDPARTY t 
  SET t.U_ID = #{uid}
WHERE t.T_OPENID = #{openid}

mapper.select:
SELECT COUNT(1) 
  FROM SYS_AUTHZ_USER_THIRDPARTY t 
 WHERE t.U_ID = #{uid}
   AND t.T_TYPE = #{type}

	AND t.T_ID != #{id}

 LIMIT 1

SELECT COUNT(1) 
  FROM SYS_AUTHZ_USER_THIRDPARTY t 
 WHERE t.U_ID = #{uid}
   AND t.T_TYPE = #{type}
  LIMIT 1
