mapper.update:
UPDATE sys_article_category t
SET c_status = #{status}
WHERE c_id = #{id}

mapper.select:
SELECT count(t.c_id)
  FROM sys_article_category t
 WHERE t.c_name = #{name}

	AND t.c_id != #{origin}

SELECT t.c_id, t.c_name, t.c_grade, t.c_intro, t.c_keywords, t.c_status, t.c_order, t.creator, (SELECT x.u_nickname from sys_authz_user_profile x where x.u_id = t.creator) as c_uname, t.create_time FROM sys_article_category t
and t.c_status = #{model.status}
ORDER By t.c_order ASC
