mapper.update:
UPDATE SYS_ARTICLE_TOPIC t 
   SET T_STATUS = #{status}
 WHERE t_id = #{id}

UPDATE SYS_ARTICLE_TOPIC t 
   SET T_NAME = #{name},
   	   T_REMARK = #{remark,jdbcType=VARCHAR},
       T_ORDER = #{order}
 WHERE t_id = #{id}

mapper.select:
SELECT count(t.t_id)
  FROM SYS_ARTICLE_TOPIC t 
 WHERE t.T_NAME = #{name}

	AND t.t_id != #{origin}
