mapper.update:
UPDATE SYS_EXTRAS_PAIRVALUE t 
   SET D_STATUS = #{status}
 WHERE D_ID = #{id}

UPDATE SYS_EXTRAS_PAIRVALUE t 
   SET D_LABEL = #{label},
   	   D_TEXT = #{value},
       D_STATUS = #{status},
       D_ORDER = #{order}
 WHERE D_ID = #{id}

    	declare
      	begin

            update SYS_EXTRAS_PAIRVALUE 
               set D_LABEL = #{item.label},
               	   D_TEXT = #{item.value},
               	   D_STATUS = #{item.status},
		       	   D_ORDER = #{item.order}
             WHERE D_ID = #{item.id} 
               AND D_GROUP = #{item.gkey} 

        end;

mapper.select:
SELECT t.D_ID, t.D_GROUP as D_GROUP_KEY, (SELECT x.G_TEXT FROM SYS_EXTRAS_PAIRGROUP x WHERE x.G_KEY = t.D_GROUP) as D_GROUP_TEXT, t.D_LABEL, t.D_KEY, t.D_TEXT, t.D_STATUS, t.D_ORDER FROM SYS_EXTRAS_PAIRVALUE t
and t.D_GROUP = #{model.gkey}
ORDER By t.D_GROUP,t.D_ORDER ASC

SELECT count(D_KEY)  
  FROM SYS_EXTRAS_PAIRVALUE t 
 WHERE t.D_GROUP = #{gkey} 
   AND t.D_KEY = #{key}

	AND t.D_ID != #{id}
