mapper.update:
UPDATE sys_data_pairvalue t
   SET d_status = #{status}
 WHERE d_id = #{id}

mapper.select:
SELECT t.d_id, t.d_group as d_group_key, (SELECT x.g_text FROM sys_data_pairgroup x WHERE x.g_key = t.d_group) as d_group_text, t.d_label, t.d_key, t.d_value, t.d_text, t.d_status, t.d_order, t.is_delete, t.creator, t.create_time, t.modifyer, t.modify_time FROM sys_data_pairvalue t
and t.d_group = #{model.gkey}
ORDER By t.d_group,t.d_order ASC

SELECT t.d_id, t.d_group as d_group_key, (SELECT x.g_text FROM sys_data_pairgroup x WHERE x.g_key = t.d_group) as d_group_text, t.d_label, t.d_key, t.d_value, t.d_text, t.d_status, t.d_order, t.is_delete, t.creator, t.create_time, t.modifyer, t.modify_time FROM sys_data_pairvalue t
t.d_group = #{item}
ORDER By t.d_group,t.d_order ASC

SELECT COUNT(1)
  FROM sys_data_pairvalue t
 WHERE t.d_group = #{gkey}
   AND t.d_key = #{key}

	AND t.d_id != #{id}

  LIMIT 1
