mapper.update:
UPDATE sys_data_pairgroup t
   SET g_status = #{status}
 WHERE g_id = #{id}

mapper.select:
SELECT COUNT(1)
  FROM sys_data_pairgroup t
 WHERE t.g_key = #{code}

	AND t.g_id != #{origin}

 AND ROWNUM = 1

SELECT COUNT(1)
  FROM sys_data_pairgroup t
 WHERE t.g_text = #{name}

	AND t.g_id != #{origin}

 AND ROWNUM = 1
