mapper.update:
UPDATE SYS_DATA_PAIRGROUP t 
   SET G_STATUS = #{status}
 WHERE G_ID = #{id}

UPDATE SYS_DATA_PAIRGROUP t 
   SET G_TEXT = #{value},
       G_STATUS = #{status}
 WHERE G_ID = #{id}

update.foreach:

 update SYS_DATA_PAIRGROUP 
    set G_TEXT = #{item.value},
    	   G_STATUS = #{item.status}
WHERE G_ID = #{item.id}

mapper.select:
SELECT COUNT(1) 
  FROM SYS_DATA_PAIRGROUP t 
 WHERE t.G_KEY = #{code}

	AND t.G_ID != #{origin}

 AND ROWNUM = 1

SELECT COUNT(1) 
  FROM SYS_DATA_PAIRGROUP t 
 WHERE t.G_TEXT = #{name}

	AND t.G_ID != #{origin}

 AND ROWNUM = 1
