mapper.update:
UPDATE SYS_DATA_PAIRVALUE t 
   SET D_STATUS = #{status}
 WHERE D_ID = #{id}

    	declare
      	begin

            update SYS_DATA_PAIRVALUE 
               set D_LABEL = #{item.label},
                   D_VALUE = #{item.value},,
               	   D_TEXT = #{item.text,jdbcType=VARCHAR},
               	   D_STATUS = #{item.status},
		       	   D_ORDER = #{item.order}
             WHERE D_ID = #{item.id} 
               AND D_GROUP = #{item.gkey} 

        end;

mapper.select:
SELECT t.D_ID, t.D_GROUP as D_GROUP_KEY, (SELECT x.G_TEXT FROM SYS_DATA_PAIRGROUP x WHERE x.G_KEY = t.D_GROUP) as D_GROUP_TEXT, t.D_LABEL, t.D_KEY, t.D_VALUE, t.D_TEXT, t.D_STATUS, t.D_ORDER FROM SYS_DATA_PAIRVALUE t
and t.D_GROUP = #{model.gkey}
and t.D_STATUS = #{model.status}
and instr(t.D_TEXT,#{model.value}) > 0
ORDER By t.D_GROUP,t.D_ORDER ASC

SELECT t.D_ID, t.D_GROUP as D_GROUP_KEY, t.D_LABEL, t.D_KEY, t.D_VALUE, t.D_TEXT, t.D_STATUS, t.D_ORDER FROM SYS_DATA_PAIRVALUE t WHERE t.D_GROUP IN
#{item}
ORDER By t.D_GROUP,t.D_ORDER ASC

SELECT COUNT(1)  
  FROM SYS_DATA_PAIRVALUE t 
 WHERE t.D_GROUP = #{gkey}
   AND t.D_KEY = #{key}

	AND t.D_ID != #{id}

   AND ROWNUM = 1

SELECT COUNT(1)
FROM SYS_DATA_PAIRVALUE t
WHERE t.D_GROUP = (SELECT D_GROUP FROM SYS_DATA_PAIRVALUE WHERE D_ID = #{id})
  AND t.D_KEY = #{key} 
  AND t.D_ID != #{id}
  AND ROWNUM = 1
