mapper.select:
SELECT t.id, t.name, t.channel, t.title, t.sign, t.content, t.template_id, t.payload, t.status, t.creator, (SELECT x.nickname FROM sys_authz_user_profile x WHERE x.id = t.creator) as t_uname, IFNULL(( SELECT COUNT( x.id ) FROM sys_inform_records x WHERE x.template_id = t.id AND x.channel = t.channel),0) AS t_total, IFNULL(( SELECT count( x.id ) FROM sys_inform_records x WHERE x.template_id = t.id AND x.channel = t.channel AND x.status = 0 ),0) AS t_unread, t.create_time FROM sys_inform_templates t
AND t.creator = #{model.userId}
AND t.channel = #{model.channel}
AND t.status = #{model.status}
AND t.title like CONCAT('%',#{model.keywords},'%')
ORDER BY t.id DESC
