mapper.select:
SELECT t.INFO_ID, t.INFO_TYPE, t.INFO_TITLE, t.INFO_DETAIL, t.INFO_STATUS, t.INFO_USERID, t.TIME24 FROM SYS_EXTRAS_INFORMS t WHERE t.INFO_USERID = #{model.userId}
AND t.INFO_TYPE = #{model.type}
AND t.INFO_STATUS = #{model.status}
AND t.INFO_TITLE like CONCAT('%',#{model.title},'%')

mapper.update:
UPDATE SYS_EXTRAS_INFORMS t 
   SET t.INFO_STATUS = #{status,jdbcType=VARCHAR}
 WHERE t.INFO_USERID = #{userId}

	and t.INFO_ID = #{id}
