mapper.select:
SELECT t.LOG_ID, t.CREATOR as LOG_USERID, IFNULL(t2.U_ALIAS,t2.U_USERNAME) as LOG_USERNAME, t.LOG_OPT, t.LOG_PROTOCOL, t.LOG_REALM, t.LOG_LEVEL, t.LOG_ADDR, t.LOG_STATUS, t.LOG_MSG, t.LOG_EXCP, t.CREATE_TIME as LOG_TIME FROM SYS_DATA_LOG_AUTHZ t, SYS_AUTHZ_USER_LIST t2 WHERE t.CREATOR = t2.U_ID
AND t.CREATOR = #{model.userId}
AND t.LOG_OPT = #{model.opt}
AND t.LOG_ADDR like CONCAT('%',#{model.addr},'%')
AND date(t.CREATE_TIME) BETWEEN date(#{model.begintime}) AND date( #{model.endtime})
AND (t2.U_USERNAME like CONCAT('%',#{model.keywords},'%') OR t2.U_ALIAS like CONCAT('%',#{model.keywords},'%') OR t2.U_PHONE like CONCAT('%',#{model.keywords},'%'))
