mapper.select:
SELECT t.LOG_id, t.CREATOR as LOG_USERid, t2.u_username as LOG_USERname, t.LOG_MODULE, t.LOG_BIZ, t.LOG_OPT, t.LOG_ADDR, t.LOG_LOCATION, t.LOG_MSG, t.LOG_EXCP, t.CREATE_TIME as LOG_TIMESTAMP FROM SYS_DATA_LOG_BIZ t INNER JOIN sys_authz_user_list t2 ON t.CREATOR = t2.u_id
AND t.CREATOR = #{model.userId}
AND t.LOG_BIZ like CONCAT('%',#{model.business},'%')
AND t.LOG_MODULE = #{model.module}
AND t.LOG_OPT = #{model.opt}
AND (t.LOG_ADDR like '%'||#{model.keywords}||'%' OR t.LOG_LOCATION like '%'||#{model.keywords}||'%' OR t2.u_username like '%'||#{model.keywords}||'%')
AND t.CREATE_TIME BETWEEN UNIX_TIMESTAMP(#{model.begintime}) AND UNIX_TIMESTAMP( #{model.endtime})
ORDER BY t.CREATE_TIME DESC
