mapper.select:
SELECT t.LOG_ID, t.LOG_MODULE, t.LOG_BIZ, t.LOG_OPT, t.CREATOR as LOG_USERID, IFNULL(t2.U_ALIAS,t2.U_USERNAME) as LOG_USERNAME, t.LOG_ADDR, t.LOG_MSG, t.LOG_EXCP, t.CREATE_TIME as LOG_TIME FROM SYS_DATA_LOG_BIZ t, SYS_AUTHZ_USER_LIST t2 WHERE t.CREATOR = t2.U_ID
and t.CREATOR = #{model.userId}
AND t.LOG_BIZ like CONCAT('%',#{model.business},'%')
and t.LOG_MODULE = #{model.module}
and t.LOG_OPT = #{model.opt}
and t.LOG_ADDR like CONCAT('%',#{model.addr},'%')
AND date(t.CREATE_TIME) BETWEEN date(#{model.begintime}) AND date( #{model.endtime})
AND (t2.U_USERNAME like CONCAT('%',#{model.keywords},'%') OR t2.U_ALIAS like CONCAT('%',#{model.keywords},'%') OR t2.U_PHONE like CONCAT('%',#{model.keywords},'%'))
ORDER BY t.CREATE_TIME DESC
