mapper.select:
SELECT t.LOG_ID, t.LOG_MODULE, t.LOG_BIZ, t.LOG_OPT, t.LOG_LEVEL, t.LOG_USERID, t.LOG_ADDR, t.LOG_MSG, t.LOG_EXCP, t.TIME24 FROM DSB_SERVICE_BIZ_LOG t
and t.LOG_USERID = #{model.userId}
and t.LOG_MODULE = #{model.module}
and t.LOG_OPT = #{model.opt}
and t.LOG_LEVEL = #{model.level}
and t.LOG_ADDR like '%'||#{model.addr}||'%'
and ( t.TIME24 >= UNIX_TIMESTAMP(#{model.begintime}) and t.TIME24 <= UNIX_TIMESTAMP(#{model.endtime}) )

SELECT t.LOG_ID, t.LOG_MODULE, t.LOG_BIZ, t.LOG_OPT, t.LOG_LEVEL, t.LOG_USERID, t.LOG_ADDR, t.LOG_MSG, t.LOG_EXCP, t.TIME24 FROM DSB_SERVICE_BIZ_LOG t WHERE (t.LOG_OPT = 'login' or t.LOG_OPT = 'logout')
and t.LOG_USERID = #{model.userId}
and t.LOG_LEVEL = #{model.level}
and t.LOG_ADDR like '%'||#{model.addr}||'%'
and ( t.TIME24 >= UNIX_TIMESTAMP(#{model.begintime}) and t.TIME24 <= UNIX_TIMESTAMP(#{model.endtime}) )
