mapper.select:
SELECT t.LOG_ID, t.CREATOR as LOG_USERID, t2.U_USERNAME as LOG_USERNAME, t.LOG_MODULE, t.LOG_BIZ, t.LOG_OPT, t.LOG_ADDR, t.LOG_LOCATION, t.LOG_MSG, t.LOG_EXCP, t.CREATE_TIME as LOG_TIME FROM SYS_DATA_LOG_BIZ t, SYS_AUTHZ_USER_LIST t2 WHERE t.CREATOR = t2.U_ID
AND t.CREATOR = #{model.userId}
AND t.LOG_MODULE = #{model.module}
AND t.LOG_BIZ like '%' || #{model.business} || '%'
AND t.LOG_OPT = #{model.opt}
AND (t.LOG_ADDR like '%'||#{model.keywords}||'%' OR t.LOG_LOCATION like '%'||#{model.keywords}||'%' OR t2.U_USERNAME like '%'||#{model.keywords}||'%')
AND to_date(t.TIME24, 'yyyy-mm-dd HH24:mi:ss') BETWEEN to_date(#{model.begintime}, 'yyyy-mm-dd HH24:mi:ss') AND to_date(#{model.endtime}, 'yyyy-mm-dd HH24:mi:ss')
ORDER BY to_date(t.TIME24, 'yyyy-mm-dd HH24:mi:ss') DESC
